package JavaClass7;

public class ForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		for(int i=1;i<50;i++) {
			if (i%2==0) {
			
				System.out.print(i+" ");
				
			}
		}
		System.out.println(" ________________________________ ");
		for(int l=1;l<50;l++) {
			if (l%2!=0) {
				System.out.print(l+" ");
				
			}
		}
			
		
		
		}
	}


